import{_ as e,c as a,o as r,ag as o}from"./chunks/framework.S-Qvb3wi.js";const p=JSON.parse('{"title":"Supported OAuth2 User Roles","description":"","frontmatter":{},"headers":[],"relativePath":"integrator-guide/oauth2/roles.md","filePath":"integrator-guide/oauth2/roles.md"}'),i={name:"integrator-guide/oauth2/roles.md"};function d(n,t,s,l,c,u){return r(),a("div",null,t[0]||(t[0]=[o('<h1 id="supported-oauth2-user-roles" tabindex="-1">Supported OAuth2 User Roles <a class="header-anchor" href="#supported-oauth2-user-roles" aria-label="Permalink to &quot;Supported OAuth2 User Roles&quot;">​</a></h1><p>This document outlines the different user roles that can be assigned to users within the Citizen iD platform. These roles can be used by integrators to manage access control and permissions within their applications.</p><div class="note custom-block github-alert"><p class="custom-block-title">Claim Availability</p><p>The <code>roles</code> claim is only included in the ID token and access token if the <code>roles</code> scope is requested during the OAuth2 authorization process.</p></div><h2 id="system-roles" tabindex="-1">System Roles <a class="header-anchor" href="#system-roles" aria-label="Permalink to &quot;System Roles&quot;">​</a></h2><p>The following roles are system-wide and can be assigned to any user. They are typically used to manage user access and permissions across the entire Citizen iD platform.</p><h3 id="user-account-status" tabindex="-1">User Account Status <a class="header-anchor" href="#user-account-status" aria-label="Permalink to &quot;User Account Status&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Claim Value</th><th>Description</th></tr></thead><tbody><tr><td><code>CitizenId.Status.Verified</code></td><td>Account has been linked with RSI and verified.</td></tr><tr><td><code>CitizenId.Status.Banned</code></td><td>Account has been suspended.</td></tr></tbody></table><h3 id="account-types" tabindex="-1">Account Types <a class="header-anchor" href="#account-types" aria-label="Permalink to &quot;Account Types&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Claim Value</th><th>Description</th></tr></thead><tbody><tr><td><code>CitizenId.AccountType.Organization</code></td><td><em>Currently unused.</em> Registered organization account.</td></tr><tr><td><code>CitizenId.AccountType.Citizen</code></td><td>Individual user account.</td></tr></tbody></table><h3 id="privileged-roles" tabindex="-1">Privileged Roles <a class="header-anchor" href="#privileged-roles" aria-label="Permalink to &quot;Privileged Roles&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Claim Value</th><th>Description</th></tr></thead><tbody><tr><td><code>CitizenId.Partner</code></td><td>Represents a trusted external partner organization.</td></tr><tr><td><code>CitizenId.Integrator</code></td><td>Entity integrating with the Citizen iD platform.</td></tr></tbody></table><h3 id="internal-roles" tabindex="-1">Internal Roles <a class="header-anchor" href="#internal-roles" aria-label="Permalink to &quot;Internal Roles&quot;">​</a></h3><p>These roles are reserved for internal use by Citizen iD staff. You should not use these roles for anything in your own applications.</p><table tabindex="0"><thead><tr><th>Claim Value</th><th>Description</th></tr></thead><tbody><tr><td><code>CitizenId.Internal.InternalSystem</code></td><td>System-level operations and automation.</td></tr><tr><td><code>CitizenId.Internal.SuperAdmin</code></td><td>Highest administrative privileges.</td></tr><tr><td><code>CitizenId.Internal.Admin</code></td><td>Standard administrative privileges.</td></tr><tr><td><code>CitizenId.Internal.Moderator</code></td><td>Content moderation and user management.</td></tr></tbody></table><h2 id="group-roles" tabindex="-1">Group Roles <a class="header-anchor" href="#group-roles" aria-label="Permalink to &quot;Group Roles&quot;">​</a></h2><div class="warning custom-block github-alert"><p class="custom-block-title">Not Yet Available</p><p>This feature is not yet available. It is planned for a future release.</p></div><hr><p><em>Last updated: October 2025</em></p>',18)]))}const m=e(i,[["render",d]]);export{p as __pageData,m as default};
